public void delete()
{
    BusinessEventsHistoryAux history;

    history.clear();
    history.initValue();

    history.BusinessEventId = this.BusinessEventId;
    history.EventId = this.EventId;
    history.EventTime = this.EventTime;
    history.SessionId = this.SessionId;
    history.SerializedContract = this.SerializedContract;
    history.LegalEntity = this.LegalEntity;
    history.State = this.State;
    history.TaskNumber = this.TaskNumber;
    history.TargetEndpoint = this.TargetEndpoint;
    history.SerializedFilterProperties = this.SerializedFilterProperties;
    history.CDSContext = this.CDSContext;
    //history.GeneratedDataBWFSPS = this.GeneratedDataBWFSPS;
    history.DescriptionAux = this.DescriptionAux;
    history.RefTableIdAux = this.RefTableIdAux;
    history.RefRecIdAux = this.RefRecIdAux;

    history.insert();

    next delete();
}
